    public static String getSafeValue(PwsRecord record, PwsFieldType aType) {
    	String fieldValue = "";
    	PwsField field = record.getField(aType);
    	if (field != null && field.getValue() != null) {
    		fieldValue = field.getValue().toString();
    	}
    	return fieldValue;
