public static PwsFile convertV2ToV1( PwsFileV2 oldFile )
throws IOException, PasswordSafeException
{
	PwsFileV1	newFile;
	PwsRecordV2	oldRec;
	PwsRecord	newRec;


	newFile = new PwsFileV1();


	newFile.setPassphrase( new StringBuilder(oldFile.getPassphrase()) );
	PwsFileStorage oldStorage = (PwsFileStorage) oldFile.getStorage();
	PwsFileStorage newStorage = new PwsFileStorage(makeNewFilename(oldStorage.getFilename(), "v1-"));
	newFile.setStorage( newStorage );
	for ( Iterator iter = oldFile.getRecords(); iter.hasNext(); )
	{
		oldRec	= (PwsRecordV2) iter.next();
		newRec	= newFile.newRecord();


		newRec.setField( oldRec.getField(PwsRecordV2.TITLE) );
		newRec.setField( oldRec.getField(PwsRecordV2.USERNAME) );
		newRec.setField( oldRec.getField(PwsRecordV2.PASSWORD) );
		newRec.setField( oldRec.getField(PwsRecordV2.NOTES) );
		newFile.add(newRec);
	}
	return newFile;

	public void testNewV1Entry () throws Exception {
		PwsFileV1	file = new PwsFileV1();


		PwsRecordV1 aRecord = (PwsRecordV1) file.newRecord() ;
		

		PwsEntryBean entry = PwsEntryBean.fromPwsRecord(aRecord);
		entry.setTitle("fromTitle");
		entry.setUsername("fromName");


		entry.toPwsRecord(aRecord);
		assertEquals("fromTitle", aRecord.getField(PwsFieldTypeV3.TITLE).toString());
		assertEquals("fromName", aRecord.getField(PwsFieldTypeV3.USERNAME).toString());


		assertEquals(entry, PwsEntryBean.fromPwsRecord(aRecord));
		entry = PwsEntryBean.fromPwsRecord(aRecord);
		assertEquals("fromTitle", entry.getTitle());
		assertEquals("fromName", entry.getUsername());
	

		entry = new PwsEntryBean();
		entry.setTitle("fromTitle");
		entry.setPassword(new StringBuilder("fromPassword"));
		aRecord = (PwsRecordV1) file.newRecord();
		entry.toPwsRecord(aRecord);
		

		assertEquals("fromPassword", aRecord.getField(PwsFieldTypeV3.PASSWORD).toString());

    @Override
	public void run() {
        // TODO: disable option if v1 or v2; URL only seems to be available in V3 files
        final PasswordSafeJFace app = PasswordSafeJFace.getApp();


        final PwsEntryBean selected = app.getSelectedRecord();
        if (selected == null)
        	return;
        

        // TODO: only fetch a filled entry if URL is not part of sparse fields.
        PwsEntryBean theEntry;
        if (selected.getUrl() != null && selected.getUrl().length() > 0) {
        	theEntry = selected;
        } else {// retrieve filled Entry for sparse
        	theEntry = app.getPwsDataStore().getEntry(selected.getStoreIndex());
        }


        Clipboard cb = new Clipboard(app.getShell().getDisplay());


        app.copyToClipboard(cb, theEntry.getUrl() );


        final IPreferenceStore thePrefs = JFacePreferences.getPreferenceStore();
        final boolean recordAccessTime =  thePrefs.getBoolean(JpwPreferenceConstants.RECORD_LAST_ACCESS_TIME);
        if (recordAccessTime) { // this could/should be sent to a background thread
        	app.updateAccessTime(theEntry);
        }


        cb.dispose();

